package logicaJava;

public class contador_ate10_do_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int contador;
		contador=10;
		
		while(contador>=0) {
			System.out.println(contador);
			contador--;
		}
		System.out.println(" terminei de contar");
	}

}
